pb <- progress_bar$new(
  format = "[:bar] :percent Time remaining: :eta",
  total = 1696  # Total number of iterations
)

i <- 0
for (col_name in align_cols) {
  i <- i + 1
  if (occupancy[i] > gap_threshold) {
    next
  } else if (occupancy[i] <= gap_threshold) {
    alignment_keep %>%
      # remove the rows which had non-gap information
      filter(grepl("-",!!sym(col_name))) %>%
      ## remove the column left behind because it was only gaps
      select(-!!sym(col_name)) -> alignment_keep
  }
  pb$tick()
}
pb$close()

# convert cleaned alignment to a fasta file
alignment_keep %>%
  unite(seq, c(2:401), sep="") -> align_fasta

# add AMR annotations to sequence names
cleaner_func <- function(df) {
  df %>%
    mutate (sequence_name := str_replace(sequence_name, ",", "")) %>%
    mutate (sequence_name := str_replace(sequence_name, "\\[", "")) %>%
    mutate (sequence_name := str_replace(sequence_name, "\\]", "")) %>%
    mutate (sequence_name := str_replace(sequence_name, "\\/[[:graph:]][[:graph:]][[:graph:]][[:graph:]][[:graph:]]", "")) %>%
    mutate (sequence_name := str_replace(sequence_name, "\\/[[:graph:]][[:graph:]][[:graph:]][[:graph:]]", "")) %>%
    mutate (sequence_name := str_replace(sequence_name, "\\/[[:graph:]][[:graph:]][[:graph:]]", ""))
}

align_cleaned <- cleaner_func(align_fasta)

# annotate gram-staining status
gram_pos <- c("Actinobacteria","Actinomycetota","Clostridiaceae","Chloroflexi","Firmicutes","Bacillota","Tenericutes")
non_bac <- c("Ascomycota","Cercozoa","Euryarchaeota","Mucoromycota","Nitrososphaerota","Dinophyceae","Uroviricota")

align_cleaned %>%
  separate_wider_delim(sequence_name, delim=":", names=c("taxid", "phylum", "family", "genus_species"), too_many="drop", too_few="align_start") %>%
  mutate(gram_status = case_when(phylum %in% gram_pos ~ "Gram positive",
                                 phylum %in% non_bac ~ "NA",
                                 TRUE ~ "Gram negative")
         ) %>%
  unite(sequence_name, c("taxid", "phylum", "family", "genus_species", "gram_status"), sep=":") -> align_fasta_annotated

write.csv(align_fasta_annotated, "temp.csv")

align_fasta_annotated %>%
  mutate(sequence_name = str_replace(sequence_name,"Rhizobium sp. AC27ram negative","Rhizobium sp. AC27:Gram negative")) %>%
  mutate(sequence_name = str_replace(sequence_name,"Rhizobium sp. AC44ram negative","Rhizobium sp. AC44:Gram negative")) %>%
  mutate(sequence_name = str_replace(sequence_name,"Streptomyces sp. NBS 14ram positive","Streptomyces sp. NBS 14:Gram positive")) %>%
  mutate(sequence_name = str_replace(sequence_name,"Flavobacteriaceae bacterium FS1-H7996am negative","Flavobacteriaceae bacterium FS1-H7996:Gram negative")) %>%
  mutate(sequence_name = str_replace(sequence_name,"Rhizobium sp. K1ram negative","Rhizobium sp. K1:Gram negative")) -> align_fasta_annotated

# export alignment
AAStringSet(align_fasta_annotated$seq) -> aligned_sequences
align_fasta_annotated$sequence_name -> names(aligned_sequences)
writeXStringSet(aligned_sequences, file="alignment_3_thresh1.0.fa", format="fasta")

# convert cleaned alignment to text file for reference
align_fasta_annotated %>%
  mutate(seq=gsub("-", "", seq)) %>%
  mutate(sequence_name=paste0(">",sequence_name)) -> align_pretxt

txt_file <- file("thresh1.0_seqs.txt", "w")

for (i in 1:nrow(align_pretxt)) {
  writeLines(paste(align_pretxt[i, "sequence_name"]), txt_file)
  writeLines(paste(align_pretxt[i, "seq"]), txt_file)
}

close(txt_file)
